mapper.update:
UPDATE content
SET like_count = like_count + 1
WHERE content_id = #{contentId}

UPDATE content
SET like_count = like_count - 1
WHERE content_id = #{contentId}

mapper.select:
SELECT like_count
FROM content
WHERE content_id = #{contentId}

mapper.update:
UPDATE content
SET favorite_count = favorite_count + 1
WHERE content_id = #{contentId}

UPDATE content
SET favorite_count = favorite_count - 1
WHERE content_id = #{contentId}

mapper.select:
SELECT favorite_count
FROM content
WHERE content_id = #{contentId}

mapper.update:
UPDATE content
SET view_count = view_count + 1
WHERE content_id = #{contentId}

mapper.select:
SELECT view_count
FROM content
WHERE content_id = #{contentId}

SELECT user_id
FROM content
WHERE content_id = #{contentId}

SELECT c.content_id, c.title, c.cover_image_url, c.view_count, c.created_time, c.type, c.favorite_count, c.like_count, c.comment_count, u.username AS author, c.description FROM content c LEFT JOIN user u ON c.user_id = u.user_id WHERE c.content_id IN
#{id}
AND c.type = #{type}
